import 'package:example/pg_response.dart';
import 'package:flutter/material.dart';
import 'package:nagad_payment_gateway/nagad_payment_gateway.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  // This widget is the root of your application.
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Flutter Demo',
      theme: ThemeData(
        colorScheme: ColorScheme.fromSeed(seedColor: Colors.deepPurple),
        useMaterial3: true,
      ),
      home: const MyHomePage(title: 'Flutter Demo Home Page'),
    );
  }
}

class MyHomePage extends StatefulWidget {
  const MyHomePage({super.key, required this.title});

  final String title;

  @override
  State<MyHomePage> createState() => _MyHomePageState();
}

class _MyHomePageState extends State<MyHomePage> {
  Nagad nagad = Nagad(
      credentials: const Credentials(
          merchantID: '683002007104225',
          merchantPrivateKey:
          '',
          pgPublicKey:
          '',
          isSandbox: true));
  var error = '';

  @override
  void initState() {
    // TODO: implement initState
    super.initState();
    Map<String, dynamic> additionalMerchantInfo = {
      'serviceName': 'Brand',
      'serviceLogoURL':
      'https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png',
      'additionalFieldNameEN': 'Type',
      'additionalFieldNameBN': 'টাইপ',
      'additionalFieldValue': 'Payment',
    };
    nagad.setAdditionalMerchantInfo(additionalMerchantInfo);
  }
  @override
  Widget build(BuildContext context) {

    return Scaffold(
      appBar: AppBar(
        backgroundColor: Theme.of(context).colorScheme.inversePrimary,
        title: Text(widget.title),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: <Widget>[
            ElevatedButton(
              child: const Text('Make Payment'),
              onPressed: () async {
                error = '';
                try {
                  StatusAPIResponse statusAPIResponse =
                      await nagad.pay(context, amount: 10);
                  Navigator.push(
                      context,
                      MaterialPageRoute(
                          builder: (context) => PGResponse(
                              statusAPIResponse: statusAPIResponse)));

                  print(statusAPIResponse);
                } catch (e) {
                  print(e);
                  setState(() {
                    error = e.toString();
                  });

                }
              },
            ),
            const SizedBox(
              height: 20,
            ),
            Text(
                error,
            )
          ],
        ),
      ),
    );
  }
}
